import React, {useState} from "react";
import {FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {faPlus} from '@fortawesome/free-solid-svg-icons'
import axios from "axios";

function SearchBar(){
    const [todoText, setTodoText] = useState("");

    function todoTextHandler(e){
        setTodoText(e.target.value);
    }

    function addTodo(){
        //axios functions to add data to db
        const todo = {
            userID: '611e92451c49a231f80a5709',
            todoText: todoText
        }

        axios.post('http://localhost:5000/todos/add', todo)
            .then((res) => {
                console.log(res);
            });

    }

    return(
        <section id="searchbar">
                <fieldset className="p-4 flex flex-row justify-center text-4xl">
                <input onChange={todoTextHandler} type="text" className=" rounded-l-lg w-1/2 shadow-md p-4  focus:outline-none "/>
                <button onClick={addTodo} className="rounded-r-lg bg-red-800  shadow-md"><FontAwesomeIcon icon={faPlus} className="text-white text-4xl m-4 "/></button> </fieldset>
        </section>
)


}



export default SearchBar;
