import React, {useState, useEffect} from "react";

export default function DayDisplay(){
    const [date, setDate] = useState("");
    useEffect(() => {

        var today = new Date();
        var month = today.toLocaleString('default', {month: 'short'});
        var day = today.toLocaleString('default', {weekday: 'long'});
        var dd = String(today.getDate());
        setDate(day + " " + month + " " + dd);
    }, [])



    return (
        <section id="day-display" className="flex flex-col items-end max-w-90 ">
            <div className="mr-80 flex flex-row items-center">
                <h1 className="text-5xl">{date} </h1>
                <h3 className="text-3xl pt-4 pl-4 opacity-40"> Today</h3>
            </div>
        </section>

    );

}
