import React, {useState, useEffect} from "react";
import {faCheck} from '@fortawesome/free-solid-svg-icons'
import {FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import axios from "axios";



export default function Todo({todoId, todoText, isMobile, todoCompleted}){
    const [completed, setCompleted] = useState(todoCompleted);



    function toggleCompleted(){
        if(!completed){
            axios.post('https:localhost:5000/todos/complete' + todoId);
        }
        else {
            axios.post('https:localhost:5000/todos/incomplete' + todoId)
        }
        setCompleted(!completed);
    }

    return(
        <section id="todo" className={`max-w-95 mx-auto transition duration-500 ease-in-out lg:max-w-60 ${completed ? "opacity-50" : ""}`} >
            <div className="flex flex-row items-center justify-between">
                <div className="m-4  w-7/12  text-left ">
                    <div className="flex gap-2 items-end">
                        <h2 className="text-2xl lg:text-4xl">{todoText}</h2>
                        {!isMobile && 
                                <h3 className="opacity-70 text-2xl">Description</h3>
                        }
                    </div>

                </div>
                {/* <div className=" m-4  flex flex-col items-end  w-1/3 rounded "> */}
                <div className="flex flex-col items-end ">

                    <div className="self-end">
                        <button onClick={toggleCompleted} className={`transition duration-500 ease-in-out opacity-1 rounded-full flex-justify-center items-center border ${!completed ?  'border-red-600' : 'border-none bg-green-800'} w-8 h-8 flex justify-end lg:w-11 lg:h-11`}>
                            {completed &&
                                    <FontAwesomeIcon icon={faCheck} className="text-white text-2xl mx-auto"/>
                            }
                        </button>
                    </div>
                    {/* <FontAwesomeIcon icon={faCircle} className="text-white border-red-800 border text-4xl "/> */}
                    {/* <div className="rounded-full self-end bg-green-600  h-10 w-10 flex-col justify-center"> */}


                    {/* </div> */}
                </div>
            </div>
            <span className="bg-main-black shadow-md w-full h-px inline-block"></span>
        </section>
    );

}
