import React from "react";
import {FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {faCalendar} from '@fortawesome/free-solid-svg-icons'
import CalendarIcon from "react-calendar-icon"; 
import { ThemeProvider } from "styled-components";


export default function Menu({showMenu}){
    const dateOptions = {
        value: {day: "2-digit"}
    }
    const calendarTheme = {
        calendarIcon: {
            textColor: "white",
            primaryColor: "#3c3b39",
            backgroundColor: "red",
        }
    };
    return(
    //may need to add x outside section, therefor need another div
        
        <section id="menu" className={`w-full h-full p-4 text-right z-10 text-3xl lg:text-5xl flex items-center transition ease-in-out duration-500 ${showMenu ? "" : "transform -translate-x-full"}`}>
            <div className="flex flex-col ml-8 justify-start gap-10" >
                <div className="flex flex-row justify-start gap-4 max-h-10">

                    {/* <FontAwesomeIcon icon={faCalendar} className="" ></FontAwesomeIcon> */}
                    <ThemeProvider theme={calendarTheme}>
                        <CalendarIcon date={new Date()}/>
                    </ThemeProvider>
                    <h2>Today</h2>
                </div>
                <div className="flex flex-row justify-start gap-4">

                    <FontAwesomeIcon icon={faCalendar} className=""/>
                    <h2>Upcoming</h2>
                </div>
                <div className="flex flex-row justify-start gap-4">

                    <FontAwesomeIcon icon={faCalendar} className=""/>
                    <h2>Notes</h2>
                </div>
                
            </div>
        </section>
    );
}
