import React from "react";


export default function MenuButton( {showMenu, setShowMenu}){
    function toggleMenu(){
       setShowMenu(!showMenu); 
    }

    return(
        <section id="menu-button" className="absolute z-20 m-2">
            <div className="inline-block" onClick={toggleMenu}>
                <div className={`bar1 w-200p h-1 p-1 rounded transition ease-in-out duration-500 lg:w-300p ${showMenu ? "transform rotate-45 translate-y-4 bg-main-black" : ""}`}></div>
                <div className={`bar2 w-200p h-1 p-1 rounded transition ease-in-out duration-500 lg:w-300p ${showMenu ? "opacity-0" : ""}`}></div>
                <div className={`bar3 w-200p h-1.5 m-2 rounded transition ease-in-out duration-500 lg:w-300p lg:m-2 ${showMenu ? " transform -rotate-45 -translate-y-3 bg-main-black" : ""}`}></div>
            </div>
        </section>
    );
}
