import React, {useState, useEffect} from "react";

export default function DayDisplay(){
    const [date, setDate] = useState("");
    useEffect(() => {
        const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
        

        var today = new Date();
        var month = today.toLocaleString('default', {month: 'long'});
        var day = String(days[today.getDay()-1]);
        var dd = String(today.getDate());
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        setDate(day + " " + dd + month);
    }, [])



    return (
        <section id="day-display" className="flex flex-col items-end">
            <div className="mr-80 flex flex-row items-center">
                <h1 className="text-5xl">{date} </h1>
                <h3 className="text-3xl pt-4 pl-4 opacity-40"> Today</h3>
            </div>
        </section>

    );

}
